scripts.confirm:
echo "
${PKG_MESSAGE}
"; read -r -p "Proceed with the changes? [y/N]";echo;if [[ $REPLY =~ ^[Yy]$ ]];then yarn proceed;fi;

scripts.proceed:
echo "$(head -n 1 CHANGELOG.md)

${PKG_MESSAGE}
$(tail -n +2 CHANGELOG.md)" > CHANGELOG.md && yarn version --no-git-tag-version --new-version $PKG_VERSION && git add . && (echo "v${PKG_VERSION}

${PKG_COMMITS}" | git commit -F -) && git tag v$PKG_VERSION && yarn push
