scripts.confirm:
echo "
${NPM_RELEASE_MESSAGE}
"; read -r -p "Proceed with the changes? [y/N]";echo;if [[ $REPLY =~ ^[Yy]$ ]];then yarn proceed;fi;

scripts.proceed:
echo "$(head -n 1 CHANGELOG.md)

${NPM_RELEASE_MESSAGE}
$(tail -n +2 CHANGELOG.md)" > CHANGELOG.md && yarn version --no-git-tag-version --new-version $NPM_RELEASE_VERSION && git add . && (echo "v${NPM_RELEASE_VERSION}

${NPM_RELEASE_COMMITS}" | git commit -F -) && git tag v$NPM_RELEASE_VERSION && yarn push
